import '../App.css';
import '../normal.css';
import { useState }  from 'react';




function Search() {

    const [input, setInput] = useState('');
  const [chatLog, setChatLog] = useState([]);
  const [question, setQuestion] = useState('');
  const [text, setText] = useState('');
  const [isLoading, setIsLoading] = useState(false);

function clearChat() {
  setChatLog([]);
}

async function handleSubmit(e) {
    e.preventDefault();
    let chatLogNew = [...chatLog, { user: "me", message: `${input}`}];
    setInput('');
    setChatLog(chatLogNew);
    setIsLoading(true);
  
    const messages = chatLogNew.map((message) => message.message);
  
    const response = await fetch('http://localhost:5000/api/chatbot', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ message: messages, text: text})
    });
  
    const data = await response.json();
    await setChatLog([...chatLogNew, { user: 'gpt', message: data.bot_message }]);
    setIsLoading(false);
  }

  return (
    
    <div className="App">
      <form onSubmit={(e) => {handleSubmit(e); toggleInfoOff();}}>
        <input className='chat-input-question' value={question} onChange={e => setQuestion(e.target.value)} />
        <input className='chat-input-text' value={text} onChange={e => setText(e.target.value)} />
        <input type="submit" className='submit-btn' value="Send"/>
      </form>
      <div>{chatLog}</div>
    </div>
  );
}

export default Search;
